import "./testimonials.scss";
import { isMobile } from 'react-device-detect';

export default function Testimonials() {
    return (
        <div className="testimonials" id='temoinages'>
            {isMobile ? (
                <>
                    <h2 className='centerText'>Désolé le jeu n'est pas disponible sur mobile.</h2>
                    <img src="https://guillaumeartiste3d.ca/wp-content/uploads/giphy.gif" alt="sad_animation" width='300' height='300' />
                </>
            ) : (
                <>
                    <h1>Jeux créé en Javascript</h1>
                    <div className='boxShadow'>
                        <iframe title='Jeux' frameBorder="0" src='assets/jeux.html' className='iframe'></iframe>
                    </div>
                </>
            )}

        </div>
    )
}
