import './works.scss';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import { travailData } from '../../data';
import { useState } from 'react';

export default function Works() {
    const [currentSlider, setCurrentSlider] = useState(0);

    return (
        <div className='works' id='travails'>
            <div className="slider" style={{ transform: `translateX(-${currentSlider * 100}vw)` }}>
                {travailData.map((item, index) => (
                    <div className="container" key={item.id}>
                        <div className="item">
                            <div className="left">
                                <div className="leftContainer">
                                    <div className="imgContainer">
                                        <img src={item.icon} alt="" />
                                    </div>
                                    <h2>{item.title}</h2>
                                    <p>
                                        {item.desc}
                                    </p>
                                    <span>{item.date}</span>
                                </div>
                            </div>
                            <div className="right">
                                <img src={item.img} alt="" />
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <ArrowBackIosIcon className="arrow left" onClick={() => handleCLick("left")} />
            <ArrowForwardIosIcon className="arrow right" onClick={() => handleCLick("left")} />

        </div>
    )
}
