import PortfolioList from '../portfolioList/PortfolioList';
import './portfolio.scss';
import { useState } from 'react';
import { mobilePortfolio, webPorfolio, wordpresPortfolio, portfolio } from '../../data.js';

export default function Portfolio() {

    const [selected, setSelected] = useState('travaux');
    const [data, setData] = useState([])

    const list = [
        {
            id: 'travaux',
            title: 'Mes travaux'
        },
        {
            id: 'mobile',
            title: 'Application Mobile'
        },
        {
            id: 'web',
            title: 'Application Web'
        },
        {
            id: 'wordpress',
            title: 'Dévelopement WordPress'
        },
    ]

    useEffect(() => {
        switch (selected) {
            case 'travaux':
                setData(portfolio)
                break;
            case 'mobile':
                setData(mobilePortfolio);
                break;
            case 'web':
                setData(webPorfolio);
                break;
            case 'wordpress':
                setData(wordpresPortfolio);
                break;
            default:
                setData(portfolio);
                break;
        }
    }, [selected])

    return (
        <div className='portfolio' id='portfolio'>
            <h1>Portfolio</h1>
            <ul>
                {list.map((item, index) => (
                    <PortfolioList
                        title={item.title}
                        active={selected === item.id}
                        setSelected={setSelected}
                        id={item.id}
                    />
                ))}
            </ul>
            <div className="container">
                <div className="item">
                    <img src="https://i2.wp.com/mookseandgripes.com/reviews/wp-content/uploads/2017/10/Barry-Lyndon-3-1024x576.png?resize=1024%2C576" alt="" />
                    <h3>Application Mobile</h3>
                </div>
                <div className="item">
                    <img src="https://i2.wp.com/mookseandgripes.com/reviews/wp-content/uploads/2017/10/Barry-Lyndon-3-1024x576.png?resize=1024%2C576" alt="" />
                    <h3>Application Mobile</h3>
                </div>
                <div className="item">
                    <img src="https://i2.wp.com/mookseandgripes.com/reviews/wp-content/uploads/2017/10/Barry-Lyndon-3-1024x576.png?resize=1024%2C576" alt="" />
                    <h3>Application Mobile</h3>
                </div>
                <div className="item">
                    <img src="https://i2.wp.com/mookseandgripes.com/reviews/wp-content/uploads/2017/10/Barry-Lyndon-3-1024x576.png?resize=1024%2C576" alt="" />
                    <h3>Application Mobile</h3>
                </div>
                <div className="item">
                    <img src="https://i2.wp.com/mookseandgripes.com/reviews/wp-content/uploads/2017/10/Barry-Lyndon-3-1024x576.png?resize=1024%2C576" alt="" />
                    <h3>Application Mobile</h3>
                </div>
            </div>
        </div>
    )
}
